import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./navbar";

const TopNews = () => {
  const [head, setHead] = useState([]);
  const url1 =
    "https://newsapi.org/v2/top-headlines?country=in&apiKey=4a9fa8a49bd44245a8f9aa3023e35bce";

  useEffect(() => {
    axios.get(url1).then((res1) => {
      setHead(res1.data.articles);
    });
  });

  return (
    <>
      {head.map((item, index) => {
        return (
          <div className="content">
            <img className="imgS" src={item.urlToImage} alt="pic"></img>
            <p className="headings">{item.title}</p>
            <a className="reading" href={item.url}>
              Continue to read...
            </a>
          </div>
        );
      })}
    </>
  );
};
export default TopNews;
