import React ,{useState} from 'react';
import './register.css';
import axios from 'axios';

const Register = () => {

      const[user,setUser] = useState({
          name: "",
          email: "",
          password: "",
          repassWord: ""
      });

     const handleClick = (e) => {
          const {name,value} = e.target;
          setUser({
              ...user,
              [name] : value
          })
      }

    const register = () => {
        const{name, email, password, repassWord} = user;
        if(name && email && password && (password === repassWord)){
          axios.post("http://localhost:9007/register", user).then(

              res => console.log(res)
          )
        }
        else{
            alert("Invalid Input or Fill all details");
        }

    }

    return(
    <>

        <div className="reg_container">
            <h2 className= "headReg">Registration</h2>

            <input type="text" name="name" value= {user.name} placeholder="Enter your name" onChange= {handleClick} />
            <input type="email" name="email" value= {user.email} placeholder="Enter your email" onChange= {handleClick} />
            <input type="password" name="password" value= {user.password} placeholder="Enter your password" onChange= {handleClick} />
            <input type="password" name="repassWord" value= {user.repassWord} placeholder="Re-enter your password" onChange= {handleClick} />
            <div className="button" onClick= {register}>Register</div>
            <div>or</div>
            <div className="button" >Login</div>
        </div>

    </>
    );
};

export default Register;
