import React from "react";
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import TopNews from "./components/topNews";
import General from "./components/general";
import Entertainment from "./components/entertainment";
import Sports from "./components/sports";
import Business from "./components/business";
import Technology from "./components/technology";
import Science from "./components/science";
import Health from "./components/health";
import Page from "./components/notfound";
import Footer from "./components/footer";
import "./App.css";
import Navbar from "./components/navbar";
import Register from "./components/register/register";
import Login from "./components/login/login";
import axios from "axios";
import LandingScreen from "./components/landingPage/landingScreen";

function App() {
  const [array, setArray] = useState([]);
  const [category, setCategory] = useState([]);

  const newsApi = async () => {
    try {
      const news = await axios.get(
        `https://newsapi.org/v2/top-headlines?country=in&category=${category}&apiKey=4a9fa8a49bd44245a8f9aa3023e35bce`
      );
      setArray(news.data.articles);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    newsApi();
  }, [category]);

  return (
    <Router>
      <Switch>
        <Route exact path="/" component={LandingScreen}></Route>
        <Route exact path="/register" component={Register}></Route>
        <Route exact path="/login" component={Login}></Route>
        <div>
          <Navbar />

          <Route exact path="/general" component={General}>
            {gen.map((item, index) => {
              return (
                <General
                  key={index}
                  rePos1={item.title}
                  rePos2={item.urlToImage}
                  rePos3={item.url}
                />
              );
            })}
          </Route>

          <Route exact path="/sports" component={Sports}>
            {spo.map((item, index) => {
              return (
                <Sports
                  key={index}
                  rePos1={item.title}
                  rePos2={item.urlToImage}
                  rePos3={item.url}
                />
              );
            })}
          </Route>

          <Footer />
        </div>

        <Route component={Page}></Route>
      </Switch>
    </Router>
  );
}

export default App;
