import React from "react";
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import General from "./components/general";
import Page from "./components/notfound";
import Footer from "./components/footer";
import "./App.css";
import Navbar from "./components/navbar";
import Register from "./components/register/register";
import Login from "./components/login/login";
import axios from "axios";
import LandingScreen from "./components/landingPage/landingScreen";

function App() {
  const [newsArray, setNewsArray] = useState([]);
  const [category, setCategory] = useState("general");

  const newsApi = async () => {
    try {
      const news = await axios.get(
        `https://newsapi.org/v2/top-headlines?country=in&category=${category}&apiKey=4a9fa8a49bd44245a8f9aa3023e35bce`
      );
      setNewsArray(news.data.articles);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    newsApi();
  });

  return (
    <Router>
      <Switch>
        <div>
          <Navbar setCat={setCategory} />

          <Route exact path="/general" component={General}>
            {<General gen={newsArray} />}
          </Route>
          <Footer />
        </div>

        <Route component={Page}></Route>
      </Switch>
    </Router>
  );
}

export default App;
