import React,{useState} from "react";
import './login.css';



const Login = () => {

   const [user,setUser] =  useState({
     email: "",
     password: ""
   });

   const handleClick = (e) => {
     const {name,value} = e.target;
     setUser({
       ...user,
       [name] : value
     })
   }

  return (
    <>
      {console.log(user)}
      <div className="log_container">
        <h2 className="headReg">Login</h2>
        <input type="email" name="email" value= {user.name} placeholder="Enter your email" onChange= {handleClick} />
        <input type= "password" name= "password" value= {user.password} placeholder= "Enter your password" onChange= {handleClick} />
        <div className= "button">Login</div>
        <div>or</div>
        <div className= "button">Register</div>
      </div>
    </>
  );
};

export default Login;
